<?phpinclude_once "../Config.php";include "../Entity/Auteur.php";class AuteurCore extends Auteur{    function afficherAut()    {        $c=Connexion::getConnexion();        $sql="select * from auteurs ";        try        {            $liste=$c->query($sql);            return $liste;        } catch (PDOException $e)        {            die( "Echec de connexion".$e->getMessage());        }    }    function afficherLivresAuteur()    {        $c=Connexion::getConnexion();        $sql="select * from livre ";        try        {            $liste=$c->query($sql);            return $liste;        } catch (PDOException $e)        {            die( "Echec de connexion".$e->getMessage());        }    }}$Aut=new AuteurCore();