<?phpinclude 'c:/wamp64/www/AvenirCulturel/Entity/Livre.php';include_once 'c:/wamp64/www/AvenirCulturel/Config.php';class LivreCore extends Livre{    public function getAll($atribute,$condition)    {        $c=Connexion::getConnexion();        $sql="select '.$atribute.' from BOOK WHERE ID='.$condition.'";        try        {            $liste=$c->query($sql);            return $liste;        } catch (PDOException $e)        {            die( "Echec de connexion".$e->getMessage());        }    }    public function afficherLivre()    {        $c=Connexion::getConnexion();        $sql="select * from BOOK ";        try        {            $liste=$c->query($sql);            return $liste;        } catch (PDOException $e)        {            die( "Echec de connexion".$e->getMessage());        }    }    public function afficherNouveaute()    {        $c=Connexion::getConnexion();        $sql="select * from BOOK ORDER BY ID DESC LIMIT 5";        try        {            $liste=$c->query($sql);            return $liste;        } catch (PDOException $e)        {            die( "Echec de connexion".$e->getMessage());        }    }    public function NbTotalLivre()    {        $c=Connexion::getConnexion();        $sql='select id from BOOK';        try{            $liste=$c->query($sql);            return $liste->rowCount();        }catch (PDOException $e)        {            die( "Echec de connexion".$e->getMessage());        }    }    public function DiviserLivre($a,$b)    {        $c=Connexion::getConnexion();        $sql="select * from BOOK ORDER BY ID DESC LIMIT ".$a.",".$b." ";        try        {            $liste=$c->query($sql);            return $liste;        } catch (PDOException $e)        {            die( "Echec de connexion".$e->getMessage());        }    }    public function DiviserLivreCategory($a,$b,$Cat)    {        $c=Connexion::getConnexion();        $sql="select * from BOOK WHERE GNERE='$Cat' ORDER BY ID DESC LIMIT ".$a.",".$b." ";        try        {            $liste=$c->query($sql);            return $liste;        } catch (PDOException $e)        {            die( "Echec de connexion".$e->getMessage());        }    }    public function NbTotalLivreCategory($cat)    {        $c=Connexion::getConnexion();        $sql="select id from BOOK WHERE GNERE='$cat' ";        try{            $liste=$c->query($sql);            return $liste->rowCount();        }catch (PDOException $e)        {            die( "Echec de connexion".$e->getMessage());        }    }    function RechercheGenre($genre)    {    }}?>