<?phpinclude "../../Config.php";function existeMail($mail){    $c=Connexion::getConnexion();    $sql="select * from compte WHERE EMAIL='$mail'  ";    try{        $liste=$c->query($sql);        if( $liste->rowCount()!=0)        {            return true;        }    }catch (PDOException $e)    {        die( "Echec de connexion".$e->getMessage());    }}if(isset($_GET['email']) ){    if( existeMail($_GET['email']) )    {        echo 1;    }    else    {        echo 4;    }}