<?phpinclude "../../Config.php";function existeLogin($compte){    $c=Connexion::getConnexion();    $sql="select * from compte WHERE LOGIN='$compte'  ";    try{        $liste=$c->query($sql);        if( $liste->rowCount()!=0)        {            return true;        }    }catch (PDOException $e)    {        die( "Echec de connexion".$e->getMessage());    }}function existeMail($mail){    $c=Connexion::getConnexion();    $sql="select * from compte WHERE EMAIL='$mail'  ";    try{        $liste=$c->query($sql);        if( $liste->rowCount()!=0)        {            return true;        }    }catch (PDOException $e)    {        die( "Echec de connexion".$e->getMessage());    }}if(isset($_GET['username']) || isset($_GET['email'])){if( existeLogin($_GET['username']) ){    echo 1;}else if( existeMail($_GET['email'])){    echo 2;}else if( existeMail($_GET['email']) && existeLogin($_GET['username'])){echo 3;}else{ echo 0;}}