<?phpif(session_status()==PHP_SESSION_NONE){    session_start();}include_once "Config.php";class AbonnementCore{    private $id_compte;    private $id_aut;    /**     * @return mixed     */    public function getIdCompte()    {        return $this->id_compte;    }    /**     * @param mixed $id_compte     */    public function setIdCompte($id_compte)    {        $this->id_compte = $id_compte;    }    /**     * @return mixed     */    public function getIdAut()    {        return $this->id_aut;    }    /**     * @param mixed $id_aut     */    public function setIdAut($id_aut)    {        $this->id_aut = $id_aut;    }function ajouter_Abonn(){$requete=Connexion::getConnexion()->prepare("INSERT INTO abonnement(ID_COMPTE,ID_AUT) VALUES(:COMPTE,:AUT)");$requete->bindValue(':COMPTE',$this->id_compte,PDO::PARAM_STR);$requete->bindValue(':AUT',$this->id_aut,PDO::PARAM_STR);if($requete->execute()){   // header("Location: index.php?ID_AUT=".$_GET['ID_AUT']);}}function supprimer_Abonn(){    $requete=Connexion::getConnexion()->prepare("DELETE FROM abonnement WHERE ID_COMPTE=:COMPTE AND ID_AUT=:AUT ");    $requete->bindValue(':COMPTE',$this->id_compte,PDO::PARAM_STR);    $requete->bindValue(':AUT',$this->id_aut,PDO::PARAM_STR);    if($requete->execute())    {  //  header("Location: index.php?ID_AUT=".$_GET['ID_AUT']);    }}function Existe(){    $c=Connexion::getConnexion();    $sql="select * from abonnement WHERE ID_COMPTE='$this->id_compte' and ID_AUT='$this->id_aut'";    try{        $liste=$c->query($sql);        if( $liste->rowCount()!=0)        {            return true;        }    }catch (PDOException $e)    {        die( "Echec de connexion".$e->getMessage());    }}function total_Abonn(){}}$abon=new AbonnementCore();if(isset($_SESSION['id']) and isset($_POST['AUT'])){$abon->setIdAut($_POST['AUT']);$abon->setIdCompte($_SESSION['id']);if($abon->Existe()){$abon->supprimer_Abonn();}else{    $abon->ajouter_Abonn();    }}