<?phpclass AuteurCore extends Auteur{    function afficherAut()    {        $c=Connexion::getConnexion();        $sql="select * from auteur ";        try        {            $liste=$c->query($sql);            return $liste;        } catch (PDOException $e)        {            die( "Echec de connexion".$e->getMessage());        }    }    function afficheLivresAuteur()    {        $c=Connexion::getConnexion();        $sql="SELECT * From book inner join auteur on book.AUTHOR=auteur.ID and auteur.ID='$this->id' ";        try        {            $liste=$c->query($sql);            return $liste;        } catch (PDOException $e)        {            die( "Echec de connexion".$e->getMessage());        }    }}$Aut=new AuteurCore();