<?phpif(session_status()==PHP_SESSION_NONE){    session_start();}include_once "Config.php";function getONE($livre,$compte){    $sql=Connexion::getConnexion()->prepare("select ID_ABONN from abonnement WHERE ID_AUT='$livre' and ID_COMPTE='$compte' ");    $sql->execute();    while($result=$sql->fetch(PDO::FETCH_ASSOC))    {        return $result['ID_ABONN'];    }}echo getONE($_GET['AUT'],$_SESSION['id']);