<?phpinclude_once "Config.php";function getONE($condition){    $c=Connexion::getConnexion();    $sql="select ID_AUT from abonnement where ID_AUT='$condition' ";    try{        $liste=$c->query($sql);        return $liste->rowCount();    }catch (PDOException $e)    {        die( "Echec de connexion".$e->getMessage());    }}if($_GET['AUT']){   echo getONE($_GET['AUT']);}