<?phpinclude "../../../../Config.php";class RankingCore{    private $star;    private $compte;    private $livre;    public function getStar()    {        return $this->star;    }    public function setStar($star)    {        $this->star = $star;    }    public function getCompte()    {        return $this->compte;    }    public function setCompte($compte)    {        $this->compte = $compte;    }    public function getLivre()    {        return $this->livre;    }    public function setLivre($livre)    {        $this->livre = $livre;    }    function existe($compte,$livre)    {        $c=Connexion::getConnexion();        $sql="select * from rating WHERE ID_COMPTE='$compte' AND ID_LIVRE='$livre' ";        try{            $liste=$c->query($sql);             if( $liste->rowCount()!=0)            {                return true;            }        }catch (PDOException $e)        {            die( "Echec de connexion".$e->getMessage());        }    }    function getRate($ID_C,$ID_L)    {        $sql=Connexion::getConnexion()->prepare("select RATE from rating WHERE ID_COMPTE='$ID_C' AND ID_LIVRE='$ID_L' ");        $sql->execute();        while($result=$sql->fetch(PDO::FETCH_ASSOC))        {            return  $result['RATE'];        }    }    function Ajouter()    {        $requete=Connexion::getConnexion()->prepare("INSERT INTO rating VALUES(:ID_C,:ID_L,:RATE)");        $requete->bindValue(':ID_C',$this->compte,PDO::PARAM_STR);        $requete->bindValue(':ID_L',$this->livre,PDO::PARAM_STR);        $requete->bindValue(':RATE',$this->star,PDO::PARAM_STR);        if($requete->execute())        {            return true;        }    }    function MetterAjour()    {        $requete=Connexion::getConnexion()->prepare("UPDATE rating SET RATE=:RATE WHERE ID_COMPTE=:ID_C AND ID_LIVRE=:ID_L");        $requete->bindValue(':ID_C',$this->compte,PDO::PARAM_STR);        $requete->bindValue(':ID_L',$this->livre,PDO::PARAM_STR);        $requete->bindValue(':RATE',$this->star,PDO::PARAM_STR);        if($requete->execute())        {            return true;        }    }    function Supprimer($compte,$livre)    {        $requete=Connexion::getConnexion()->prepare("DELETE FROM rating WHERE ID_COMPTE= '$compte' AND ID_LIVRE='$livre' ");        if($requete->execute())        {            return true;        }    }}?>