<?phpinclude "../../../Entity/Compte.php";include '../../../Config.php';if(session_status()==PHP_SESSION_NONE){    session_start();}class CompteConfig extends Compte{    function getEMAIL($id)    {        $sql=Connexion::getConnexion()->prepare("select EMAIL from compte WHERE LOGIN='$id'");        $sql->execute();        while($result=$sql->fetch(PDO::FETCH_ASSOC))        {            return $result['EMAIL'];        }    }    function getPASSWORD($id)    {        $sql=Connexion::getConnexion()->prepare("select MDP from compte WHERE LOGIN='$id'");        $sql->execute();        while($result=$sql->fetch(PDO::FETCH_ASSOC))        {            return $result['MDP'];        }    }    function getData($id,$c)    {        $sql=Connexion::getConnexion()->prepare("select $c from compte WHERE LOGIN='$id'");        $sql->execute();        while($result=$sql->fetch(PDO::FETCH_ASSOC))        {            return $result[$c];        }    }    function mettreAjour()    {        $requete=Connexion::getConnexion()->prepare("UPDATE compte SET LOGIN=:ID_C,MDP=:MDP,EMAIL=:MAIL,NOM=:NOM,PRENOM=:PRENOM,DATE_NAISS=:DATE_NAISS,ADRESSE=:ADRESSE,TEL=:TEL WHERE LOGIN=:ID_C");        $requete->bindValue(':ID_C',$this->username,PDO::PARAM_STR);        $requete->bindValue(':MDP', $this->password,PDO::PARAM_STR);        $requete->bindValue(':MAIL',$this->email,PDO::PARAM_STR);        $requete->bindValue(':NOM', $this->nom,PDO::PARAM_STR);        $requete->bindValue(':PRENOM',$this->prenom,PDO::PARAM_STR);        $requete->bindValue(':ADRESSE',$this->adresse,PDO::PARAM_STR);        $requete->bindValue(':DATE_NAISS',$this->datenaiss,PDO::PARAM_STR);        $requete->bindValue(':TEL',$this->tel,PDO::PARAM_STR);        if($requete->execute())        {            return true;        }    }    function mettreAjourMotDepasse()    {        $requete=Connexion::getConnexion()->prepare("UPDATE compte SET MDP=:MDP WHERE LOGIN=:ID_C");        $requet